contracts/MyOApp.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.22;

import { OApp, Origin, MessagingFee } from "@layerzerolabs/oapp-evm/contracts/oapp/OApp.sol";
import { OAppOptionsType3 } from "@layerzerolabs/oapp-evm/contracts/oapp/libs/OAppOptionsType3.sol";
import { OptionsBuilder } from "@layerzerolabs/oapp-evm/contracts/oapp/libs/OptionsBuilder.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

contract OmniDaoController is OApp, OAppOptionsType3, ReentrancyGuard {
    using OptionsBuilder for bytes;

    struct Proposal {
        uint256 proposalId;
        address proposer;
        string description;
        uint256 votesFor;
        uint256 votesAgainst;
        uint256 endTime;
        uint256 startTime;
        bool executed;
        ProposalType proposalType;
        bytes executionData;
        uint32[] targetChainIds;
        bytes32[] remoteCallData;
    }

    enum VoteType {
        FOR,
        AGAINST,
        ABSTAIN
    }

    enum ProposalType {
        TREASURY_DELEGATION,
        EMERGENCY,
        GOVERNANCE_CHANGE,
        PROTOCOL_UPGRADE,
        PARAMETER_CHANGE,
        OTHER
    }

    mapping(uint256 => Proposal) public proposals;
    mapping(uint256 => mapping(address => VoteType)) public votes;
    mapping(uint256 => mapping(address => bool)) public hasVoted;
    mapping(address => uint256) public votingPower;
    mapping(uint32 => bool) public supportedChains;
    mapping(uint32 => address) public remoteExecutors;
    mapping(bytes32 => bool) public executedOperations;
    mapping(uint32 => uint256) public lastExecutionNonce;

    uint256 public proposalCount;
    uint256 public votingPeriod;
    uint256 public minimumVotingPower;
    uint256 public quorumThreshold;
    uint256 public totalVotingPower;

    bool public isPaused;
    mapping(address => bool) public isPausable;

    // Array to track all supported chain IDs for iteration
    uint32[] public supportedChainsList;

    event ProposalCreated(uint256 indexed proposalId, address indexed proposer, string description, ProposalType proposalType);
    event VoteCast(uint256 indexed proposalId, address indexed voter, VoteType vote);
    event ProposalExecuted(uint256 indexed proposalId);
    event ProposalCanceled(uint256 indexed proposalId);
    event VotingPowerUpdated(address indexed voter, uint256 newVotingPower);
    event ChainSupportUpdated(uint32 indexed chainId, bool supported);
    event RemoteExecutorUpdated(uint32 indexed chainId, address executor);
    event Paused(bool isPaused);
    event RemoteOperationSent(uint32 indexed chainId, address indexed executor, bytes32 indexed operationId, bytes data);
    event EmergencyAction(address indexed executor, string action, uint32[] chains);

    /// @notice Msg type for sending a string, for use in OAppOptionsType3 as an enforced option
    uint16 public constant SEND = 1;

    constructor(address _endpoint, address _owner) OApp(_endpoint, _owner) Ownable(_owner) {}

    /**
     * @notice Quotes the gas needed to pay for the full omnichain transaction in native gas or ZRO token.
     * @param _dstEid Destination chain's endpoint ID.
     * @param _string The string to send.
     * @param _options Message execution options (e.g., for sending gas to destination).
     * @param _payInLzToken Whether to return fee in ZRO token.
     * @return fee A `MessagingFee` struct containing the calculated gas fee in either the native token or ZRO token.
     */
    function quoteSendString(
        uint32 _dstEid,
        string calldata _string,
        bytes calldata _options,
        bool _payInLzToken
    ) public view returns (MessagingFee memory fee) {
        bytes memory _message = abi.encode(_string);
        fee = _quote(_dstEid, _message, combineOptions(_dstEid, SEND, _options), _payInLzToken);
    }

    /**
     * @notice Create a new proposal
     * @param description Human readable description
     * @param proposalType Type of proposal
     * @param executionData Local execution data (if any)
     * @param targetChainIds Array of chain IDs to execute on
     * @param remoteCallData Array of calldata for each target chain
     */
    function createProposal(
        string memory description,
        ProposalType proposalType,
        bytes memory executionData,
        uint32[] memory targetChainIds,
        bytes32[] memory remoteCallData
    ) external {
        require(votingPower[msg.sender] >= minimumVotingPower, "Insufficient voting power");
        require(targetChainIds.length == remoteCallData.length, "Array length mismatch");
        require(!isPaused, "Contract is paused for emergency");

        uint256 proposalId = ++proposalCount;

        proposals[proposalId] = Proposal({
            proposalId: proposalId,
            proposer: msg.sender,
            description: description,
            votesFor: 0,
            votesAgainst: 0,
            startTime: block.timestamp,
            endTime: block.timestamp + votingPeriod,
            executed: false,
            proposalType: proposalType,
            executionData: executionData,
            targetChainIds: targetChainIds,
            remoteCallData: remoteCallData
        });

        emit ProposalCreated(proposalId, msg.sender, description, proposalType);
    }

    /**
     * @notice Cast a vote on a proposal
     * @param proposalId ID of the proposal to vote on
     * @param voteType Type of vote (FOR, AGAINST, ABSTAIN)
     */
    function vote(uint256 proposalId, VoteType voteType) external {
        Proposal storage proposal = proposals[proposalId];
        require(proposal.proposalId != 0, "Proposal does not exist");
        require(block.timestamp <= proposal.endTime, "Voting period ended");
        require(!hasVoted[proposalId][msg.sender], "Already voted");
        require(votingPower[msg.sender] > 0, "No voting power");

        hasVoted[proposalId][msg.sender] = true;
        votes[proposalId][msg.sender] = voteType;

        uint256 weight = votingPower[msg.sender];

        if (voteType == VoteType.FOR) {
            proposal.votesFor += weight;
        } else if (voteType == VoteType.AGAINST) {
            proposal.votesAgainst += weight;
        }

        emit VoteCast(proposalId, msg.sender, voteType);
    }

    /**
     * @notice Execute a passed proposal
     * @param proposalId ID of the proposal to execute
     */
    function executeProposal(uint256 proposalId) external payable nonReentrant {
        Proposal storage proposal = proposals[proposalId];
        require(proposal.proposalId != 0, "Proposal does not exist");
        require(block.timestamp > proposal.endTime, "Voting period not ended");
        require(!proposal.executed, "Already executed");
        require(_proposalPassed(proposalId), "Proposal did not pass");

        proposal.executed = true;

        if (proposal.executionData.length > 0) {
            _executeLocal(proposal.executionData);
        }

        bool success = _executeRemote(proposalId, proposal);

        emit ProposalExecuted(proposalId);
    }

    /**
     * @notice Execute a proposal on remote chains
     * @param proposalId ID of the proposal to execute
     * @param proposal Proposal struct
     * @return bool True if execution was successful
     */
    function _executeRemote(
        uint256 proposalId,
        Proposal storage proposal
    ) internal returns (bool) {
       require(msg.value > 0, "No funds to cover gas");

       uint256 totalChains = proposal.targetChainIds.length;
       uint256 gasPerChain = msg.value / totalChains;

       for (uint256 i = 0; i < totalChains; i++) {
        uint32 targetChain = proposal.targetChainIds[i];
        require(supportedChains[targetChain], "Unsupported chain");
        require(remoteExecutors[targetChain] != address(0), "No executor on target chain");

        bytes memory message = abi.encode(proposalId, proposal.proposalType, proposal.remoteCallData[i], block.timestamp);

        bytes32 operationHash = keccak256(abi.encodePacked(proposalId, targetChain, i));

        // Fixed OptionsBuilder usage
        bytes memory options = OptionsBuilder.newOptions()
            .addExecutorLzReceiveOption(200000, 0)
            .addExecutorOrderedExecutionOption();

        MessagingFee memory fee = MessagingFee(gasPerChain, 0);

        _lzSend(targetChain, message, options, fee, payable(msg.sender));

        executedOperations[operationHash] = true;
        lastExecutionNonce[targetChain]++;

        emit RemoteOperationSent(targetChain, remoteExecutors[targetChain], operationHash, abi.encodePacked(proposal.remoteCallData[i]));       }

       return true;
    }

    /**
     * @notice Execute a proposal on the local chain
     * @param executionData Encoded execution data
     */
    function _executeLocal(bytes memory executionData) internal {
        // For testing, just emit an event
        // In production, implement actual local execution logic
        emit LocalExecutionCompleted(executionData);
    }

    // Add missing event
    event LocalExecutionCompleted(bytes executionData);

    /**
     * @notice Pause the contract for emergency
     */
    function emergencyPause() external {
        require(!isPaused, "Already paused");
        require(isPausable[msg.sender], "Not an emergency guardian");

        isPaused = true;
        emit Paused(true);

        _sendEmergencyAction("PAUSE_ALL", "Emergency pause activated");
    }

    function emergencyUnpause() external {
        require(isPaused, "Not paused");
        require(isPausable[msg.sender], "Not an emergency guardian");

        isPaused = false;
        _sendEmergencyAction("UNPAUSE_ALL", "Emergency pause deactivated");
        emit Paused(false);
    }

    /**
     * @notice Send an emergency action to all supported chains
     * @param action The action to send
     * @param reason The reason for the action
     */
    function _sendEmergencyAction(string memory action, string memory reason) internal {
        uint32[] memory allChains = _getAllSupportedChains();

        for (uint256 i = 0; i < allChains.length; i++) {
            uint32 chainId = allChains[i];
            if (remoteExecutors[chainId] != address(0)) {
                bytes memory message = abi.encode("EMERGENCY", action, reason, block.timestamp);

                // Fixed OptionsBuilder usage
                bytes memory options = OptionsBuilder.newOptions()
                    .addExecutorLzReceiveOption(100000, 0);

                MessagingFee memory fee = MessagingFee(0.001 ether, 0);

                _lzSend(chainId, message, options, fee, payable(msg.sender));
            }
        }

        emit EmergencyAction(msg.sender, action, allChains);
    }

    /**
     * @notice Set a remote executor for a chain
     * @param chainId The chain ID to set the executor for
     * @param executor The executor address
     */
    function setRemoteExecutor(uint32 chainId, address executor) external onlyOwner {
        // Add to supported chains list if not already present
        if (!supportedChains[chainId]) {
            supportedChainsList.push(chainId);
        }

        remoteExecutors[chainId] = executor;
        supportedChains[chainId] = true;

        emit RemoteExecutorUpdated(chainId, executor);
    }

    /**
     * @notice Update the voting power for an account
     * @param account The account to update the voting power for
     * @param power The new voting power
     */
    function updateVotingPower(address account, uint256 power) external onlyOwner {
        uint256 oldPower = votingPower[account];
        votingPower[account] = power;

        totalVotingPower = totalVotingPower - oldPower + power;

        emit VotingPowerUpdated(account, power);
    }

    /**
     * @notice Set the governance parameters
     * @param _votingPeriod The voting period
     * @param _quorumThreshold The quorum threshold
     * @param _minVotingPower The minimum voting power
     */
    function setGovernanceParams(
        uint256 _votingPeriod,
        uint256 _quorumThreshold,
        uint256 _minVotingPower
    ) external onlyOwner {
        votingPeriod = _votingPeriod;
        quorumThreshold = _quorumThreshold;
        minimumVotingPower = _minVotingPower;
    }

    /**
     * @notice Check if a proposal has passed
     * @param proposalId ID of the proposal to check
     * @return bool True if the proposal has passed
     */
    function _proposalPassed(uint256 proposalId) internal view returns (bool) {
        Proposal memory proposal = proposals[proposalId];
        uint256 totalVotes = proposal.votesFor + proposal.votesAgainst;

        // Check quorum
        if (totalVotes < (totalVotingPower * quorumThreshold) / 10000) {
            return false;
        }

        // Check majority
        return proposal.votesFor > proposal.votesAgainst;
    }

    /**
     * @notice Get a proposal
     * @param proposalId ID of the proposal to get
     * @return Proposal struct
     */
    function getProposal(uint256 proposalId) external view returns (Proposal memory) {
        return proposals[proposalId];
    }

    /**
     * @notice Get the voting power for an account
     * @param account The account to get the voting power for
     * @return uint256 The voting power for the account
     */
    function getVotingPower(address account) external view returns (uint256) {
        return votingPower[account];
    }

    /**
     * @notice Quote the gas needed to send a message
     * @param targetChain The target chain ID
     * @param message The message to send
     * @param options The options for the message
     * @param payInLzToken Whether to pay in ZRO token
     * @return MessagingFee The gas fee
     */
    function quote(
        uint32 targetChain,
        bytes memory message,
        bytes memory options,
        bool payInLzToken
    ) public view returns (MessagingFee memory) {
        return _quote(targetChain, message, options, payInLzToken);
    }

    /**
     * @notice Get the next nonce for a chain
     * @param srcEid The source chain ID
     * @param sender The sender address
     * @return uint64 The next nonce
     */
    function nextNonce(uint32 srcEid, bytes32 sender) public view virtual override returns (uint64) {
        return endpoint.outboundNonce(address(this), srcEid, sender);
    }

    /**
     * @notice Receive ETH
     */
    receive() external payable {}

    /**
     * @notice Send a string to a remote OApp on another chain
     * @param _dstEid Destination Endpoint ID (uint32)
     * @param _string The string to send
     * @param _options Execution options for gas on the destination (bytes)
     */
    function sendString(uint32 _dstEid, string calldata _string, bytes calldata _options) external payable {
        bytes memory _message = abi.encode(_string);

        _lzSend(
            _dstEid,
            _message,
            combineOptions(_dstEid, SEND, _options),
            MessagingFee(msg.value, 0),
            payable(msg.sender)
        );
    }

    /**
     * @notice Invoked by OAppReceiver when EndpointV2.lzReceive is called
     * @param _origin Metadata (source chain, sender address, nonce)
     * @param _guid Global unique ID for tracking this message
     * @param _message ABI-encoded bytes (the string we sent earlier)
     * @param _executor Executor address that delivered the message
     * @param _extraData Additional data from the Executor (unused here)
     */
    function _lzReceive(
        Origin calldata _origin,
        bytes32 _guid,
        bytes calldata _message,
        address _executor,
        bytes calldata _extraData
    ) internal override {
        require(
            remoteExecutors[_origin.srcEid] != address(0) &&
            _origin.sender == bytes32(uint256(uint160(remoteExecutors[_origin.srcEid]))),
            "Unauthorized sender"
        );

        // Decode the response
        (uint256 proposalId, bool success, string memory result) = abi.decode(
            _message,
            (uint256, bool, string)
        );

        // Handle the response (emit events, update state, etc.)
        // emit RemoteOperationSent(proposalId, _origin.srcEid, keccak256(_message), _message);
    }

    /**
     * @notice Get all supported chains
     * @return uint32[] Array of supported chain IDs
     */
    function _getAllSupportedChains() internal view returns (uint32[] memory) {
        uint32[] memory result = new uint32[](supportedChainsList.length);
        uint256 count = 0;

        for (uint256 i = 0; i < supportedChainsList.length; i++) {
            uint32 chainId = supportedChainsList[i];
            if (supportedChains[chainId] && remoteExecutors[chainId] != address(0)) {
                result[count] = chainId;
                count++;
            }
        }

        // Resize array to actual count
        uint32[] memory finalResult = new uint32[](count);
        for (uint256 i = 0; i < count; i++) {
            finalResult[i] = result[i];
        }

        return finalResult;
    }

    /**
     * @notice Get all supported chains (external function)
     * @return uint32[] Array of supported chain IDs
     */
    function getAllSupportedChains() external view returns (uint32[] memory) {
        return _getAllSupportedChains();
    }
}

contracts/RemoteExecutor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import { OApp, Origin, MessagingFee } from "@layerzerolabs/oapp-evm/contracts/oapp/OApp.sol";
import { OAppOptionsType3 } from "@layerzerolabs/oapp-evm/contracts/oapp/libs/OAppOptionsType3.sol";
import { OptionsBuilder } from "@layerzerolabs/oapp-evm/contracts/oapp/libs/OptionsBuilder.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

// Add these interfaces
interface ILendingProtocol {
    function setInterestRate(uint256 rate) external;
    function updateCollateralRatio(address token, uint256 ratio) external;
}

interface IAMMPool {
    function setTradingFee(uint256 fee) external;
}

interface IStaking {
    function stake(uint256 amount) external;
}

interface IPausable {
    function pause() external;
    function unpause() external;
}

/**
 * @title RemoteExecutor
 * @notice Remote executor contract deployed on each target chain
 * @dev Receives and executes commands from the master DAO controller
 */
contract RemoteExecutor is OApp, OAppOptionsType3, ReentrancyGuard {
    using OptionsBuilder for bytes;

    // ============ STATE VARIABLES ============

    struct ExecutionRecord {
        uint256 proposalId;
        uint256 timestamp;
        bool success;
        string result;
    }

    enum ProposalType {
        PARAMETER_UPDATE,
        TREASURY_OPERATION,
        EMERGENCY_ACTION,
        GOVERNANCE_CHANGE,
        PROTOCOL_UPGRADE
    }

    // Master controller (on Ethereum)
    uint32 public immutable masterChainId;
    address public masterController;

    // Local protocol contracts this executor can control
    mapping(string => address) public protocolContracts;
    mapping(bytes32 => ExecutionRecord) public executionHistory;

    // Emergency controls
    bool public paused;
    mapping(address => bool) public emergencyGuardians;

    // Execution tracking
    uint256 public executionCount;
    mapping(uint256 => bool) public processedProposals;

    // ============ EVENTS ============

    event CommandReceived(
        uint256 indexed proposalId,
        ProposalType indexed proposalType,
        address indexed executor
    );

    event CommandExecuted(
        uint256 indexed proposalId,
        bool success,
        string result
    );

    event EmergencyActionExecuted(
        string action,
        string reason,
        address executor
    );

    event ProtocolContractUpdated(
        string indexed contractName,
        address indexed contractAddress
    );

    // ============ CONSTRUCTOR ============

    constructor(
        address _endpoint,
        address _delegate,
        uint32 _masterChainId,
        address _masterController
    ) OApp(_endpoint, _delegate) Ownable(_delegate) {
        masterChainId = _masterChainId;
        masterController = _masterController;
        emergencyGuardians[_delegate] = true;
    }

    // ============ LAYERZERO RECEIVE FUNCTION ============

    /**
     * @notice Handle incoming messages from master controller
     * @param _origin Origin information
     * @param _guid Message GUID
     * @param _message Encoded message
     * @param _executor Executor address
     * @param _extraData Extra data
     */
    function _lzReceive(
        Origin calldata _origin,
        bytes32 _guid,
        bytes calldata _message,
        address _executor,
        bytes calldata _extraData
    ) internal override {
        // Verify message comes from master controller
        require(_origin.srcEid == masterChainId, "Invalid source chain");
        require(
            _origin.sender == bytes32(uint256(uint160(masterController))),
            "Unauthorized sender"
        );

        // Decode the message
        string memory messageType = _getMessageType(_message);

        if (keccak256(bytes(messageType)) == keccak256("EMERGENCY")) {
            _handleEmergencyAction(_message);
        } else {
            _handleProposalExecution(_message);
        }
    }

    /**
     * @notice Handle proposal execution
     * @param _message Encoded message with proposal data
     */
    function _handleProposalExecution(bytes calldata _message) internal {
        (
            uint256 proposalId,
            ProposalType proposalType,
            bytes memory executionData,
            uint256 timestamp
        ) = abi.decode(_message, (uint256, ProposalType, bytes, uint256));

        require(!processedProposals[proposalId], "Proposal already processed");
        require(!paused, "Contract is paused");

        processedProposals[proposalId] = true;
        executionCount++;

        emit CommandReceived(proposalId, proposalType, msg.sender);

        // Execute the command based on type
        (bool success, string memory result) = _executeCommand(
            proposalType,
            executionData
        );

        // Record execution
        bytes32 executionHash = keccak256(abi.encodePacked(proposalId, timestamp));
        executionHistory[executionHash] = ExecutionRecord({
            proposalId: proposalId,
            timestamp: timestamp,
            success: success,
            result: result
        });

        emit CommandExecuted(proposalId, success, result);

        // Send response back to master controller
        _sendResponse(proposalId, success, result);
    }

    /**
     * @notice Handle emergency actions
     * @param _message Encoded emergency message
     */
    function _handleEmergencyAction(bytes calldata _message) internal {
        (, string memory action, string memory reason,) = abi.decode(
            _message,
            (string, string, string, uint256)
        );

        if (keccak256(bytes(action)) == keccak256("PAUSE_ALL")) {
            paused = true;
        } else if (keccak256(bytes(action)) == keccak256("UNPAUSE_ALL")) {
            paused = false;
        }

        emit EmergencyActionExecuted(action, reason, msg.sender);
    }

    // ============ COMMAND EXECUTION FUNCTIONS ============

    /**
     * @notice Execute a command based on proposal type
     * @param proposalType Type of proposal
     * @param executionData Encoded execution data
     * @return success Whether execution succeeded
     * @return result Result message
     */
    function _executeCommand(
        ProposalType proposalType,
        bytes memory executionData
    ) internal returns (bool success, string memory result) {
        if (proposalType == ProposalType.PARAMETER_UPDATE) {
            return _executeParameterUpdate(executionData);
        } else if (proposalType == ProposalType.TREASURY_OPERATION) {
            return _executeTreasuryOperation(executionData);
        } else if (proposalType == ProposalType.EMERGENCY_ACTION) {
            return _executeEmergencyAction(executionData);
        } else if (proposalType == ProposalType.GOVERNANCE_CHANGE) {
            return _executeGovernanceChange(executionData);
        } else if (proposalType == ProposalType.PROTOCOL_UPGRADE) {
            return _executeProtocolUpgrade(executionData);
        }

        return (false, "Unknown proposal type");
    }

    /**
     * @notice Execute parameter updates
     * @param executionData Encoded parameter update data
     */
    function _executeParameterUpdate(bytes memory executionData) internal returns (bool, string memory) {
        try this._decodeAndExecuteParameterUpdate(executionData) {
            return (true, "Parameter update successful");
        } catch Error(string memory reason) {
            return (false, reason);
        } catch {
            return (false, "Parameter update failed");
        }
    }

    /**
     * @notice Decode and execute parameter update
     * @param executionData Encoded data
     */
    function _decodeAndExecuteParameterUpdate(bytes memory executionData) external {
        require(msg.sender == address(this), "Internal call only");

        (string memory contractName, string memory functionName, bytes memory callData) = 
            abi.decode(executionData, (string, string, bytes));

        address targetContract = protocolContracts[contractName];
        require(targetContract != address(0), "Contract not found");

        // Example parameter updates
        if (keccak256(bytes(functionName)) == keccak256("setInterestRate")) {
            uint256 newRate = abi.decode(callData, (uint256));
            ILendingProtocol(targetContract).setInterestRate(newRate);
        } else if (keccak256(bytes(functionName)) == keccak256("updateCollateralRatio")) {
            (address token, uint256 ratio) = abi.decode(callData, (address, uint256));
            ILendingProtocol(targetContract).updateCollateralRatio(token, ratio);
        } else if (keccak256(bytes(functionName)) == keccak256("setTradingFee")) {
            uint256 newFee = abi.decode(callData, (uint256));
            IAMMPool(targetContract).setTradingFee(newFee);
        }
    }

    /**
     * @notice Execute treasury operations
     * @param executionData Encoded treasury operation data
     */
    function _executeTreasuryOperation(bytes memory executionData) internal returns (bool, string memory) {
        try this._decodeAndExecuteTreasuryOperation(executionData) {
            return (true, "Treasury operation successful");
        } catch Error(string memory reason) {
            return (false, reason);
        } catch {
            return (false, "Treasury operation failed");
        }
    }

    /**
     * @notice Decode and execute treasury operation
     * @param executionData Encoded data
     */
    function _decodeAndExecuteTreasuryOperation(bytes memory executionData) external {
        require(msg.sender == address(this), "Internal call only");

        (string memory operation, bytes memory operationData) = 
            abi.decode(executionData, (string, bytes));

        if (keccak256(bytes(operation)) == keccak256("transferFunds")) {
            (address token, address to, uint256 amount) = 
                abi.decode(operationData, (address, address, uint256));

            if (token == address(0)) {
                // Transfer ETH
                payable(to).transfer(amount);
            } else {
                // Transfer ERC20
                IERC20(token).transfer(to, amount);
            }
        } else if (keccak256(bytes(operation)) == keccak256("stakeFunds")) {
            (address stakingContract, uint256 amount) = 
                abi.decode(operationData, (address, uint256));
            IStaking(stakingContract).stake(amount);
        }
    }

    /**
     * @notice Execute emergency actions
     * @param executionData Encoded emergency data
     */
    function _executeEmergencyAction(bytes memory executionData) internal returns (bool, string memory) {
        (string memory action, address targetContract) = 
            abi.decode(executionData, (string, address));

        if (keccak256(bytes(action)) == keccak256("pauseContract")) {
            IPausable(targetContract).pause();
            return (true, "Contract paused");
        } else if (keccak256(bytes(action)) == keccak256("unpauseContract")) {
            IPausable(targetContract).unpause();
            return (true, "Contract unpaused");
        }

        return (false, "Unknown emergency action");
    }

    /**
     * @notice Execute governance changes
     * @param executionData Encoded governance data
     */
    function _executeGovernanceChange(bytes memory executionData) internal returns (bool, string memory) {
        // Implementation for governance changes
        return (true, "Governance change executed");
    }

    /**
     * @notice Execute protocol upgrades
     * @param executionData Encoded upgrade data
     */
    function _executeProtocolUpgrade(bytes memory executionData) internal returns (bool, string memory) {
        // Implementation for protocol upgrades
        return (true, "Protocol upgrade executed");
    }

    // ============ UTILITY FUNCTIONS ============

    /**
     * @notice Get message type from encoded message
     * @param _message Encoded message
     * @return Message type string
     */
    function _getMessageType(bytes calldata _message) internal pure returns (string memory) {
        // Simple message type detection
        if (_message.length > 32) {
            (string memory messageType) = abi.decode(_message[:32], (string));
            return messageType;
        }
        return "PROPOSAL";
    }

    /**
     * @notice Send response back to master controller
     * @param proposalId ID of the executed proposal
     * @param success Whether execution succeeded
     * @param result Result message
     */
    function _sendResponse(
        uint256 proposalId,
        bool success,
        string memory result
    ) internal {
        bytes memory message = abi.encode(proposalId, success, result);

        bytes memory options = OptionsBuilder.newOptions()
            .addExecutorLzReceiveOption(100000, 0);

        MessagingFee memory fee = MessagingFee(0, 0); // No fee for response

        _lzSend(masterChainId, message, options, fee, payable(address(this)));
    }

    // ============ ADMIN FUNCTIONS ============

    /**
     * @notice Set a protocol contract address
     * @param contractName Name of the contract
     * @param contractAddress Address of the contract
     */
    function setProtocolContract(string memory contractName, address contractAddress) external onlyOwner {
        protocolContracts[contractName] = contractAddress;
        emit ProtocolContractUpdated(contractName, contractAddress);
    }

    /**
     * @notice Update master controller address
     * @param newMasterController New master controller address
     */
    function updateMasterController(address newMasterController) external onlyOwner {
        masterController = newMasterController;
    }

    /**
     * @notice Add emergency guardian
     * @param guardian Guardian address
     */
    function addEmergencyGuardian(address guardian) external onlyOwner {
        emergencyGuardians[guardian] = true;
    }

    /**
     * @notice Remove emergency guardian
     * @param guardian Guardian address
     */
    function removeEmergencyGuardian(address guardian) external onlyOwner {
        emergencyGuardians[guardian] = false;
    }

    /**
     * @notice Emergency pause (guardian only)
     */
    function emergencyPause() external {
        require(emergencyGuardians[msg.sender], "Not an emergency guardian");
        paused = true;
    }

    /**
     * @notice Emergency unpause (owner only)
     */
    function emergencyUnpause() external onlyOwner {
        paused = false;
    }

    /**
     * @notice Withdraw stuck tokens
     * @param token Token address (address(0) for ETH)
     * @param to Recipient address
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address token, address to, uint256 amount) external onlyOwner {
        if (token == address(0)) {
            payable(to).transfer(amount);
        } else {
            IERC20(token).transfer(to, amount);
        }
    }

    /**
     * @notice Receive ETH
     */
    receive() external payable {}
}

